mapper.insert:
INSERT INTO TRADE (TRADENUM, TRADEDATE, TRADEAMOUNT, TRADEBALANCE, TRADEIO, ACCOUNTNUMBER) VALUES ( TRADE_SEQ.NEXTVAL, SYSDATE, #{tradeAmount},
(SELECT ACCOUNTBALANCE FROM ACCOUNT WHERE ACCOUNTNUMBER=#{receiveNumber})+#{tradeAmount}, #{tradeIO}, #{receiveNumber}
(SELECT ACCOUNTBALANCE FROM ACCOUNT WHERE ACCOUNTNUMBER=#{accountNumber})-#{tradeAmount}, #{tradeIO}, #{accountNumber}
)

mapper.update:

  			UPDATE ACCOUNT SET 

	  			ACCOUNTBALANCE=ACCOUNTBALANCE+#{tradeAmount}
	  			WHERE ACCOUNTNUMBER=#{receiveNumber}



  				ACCOUNTBALANCE=ACCOUNTBALANCE-#{tradeAmount}
	  			WHERE ACCOUNTNUMBER=#{accountNumber}
